{"changed":true,"filter":false,"title":"stock_picker.rb","tooltip":"/stock_picker.rb","value":"#takes array of prices and returns a pair of days with the best price to buy and cell\n\nprices = [17, 2, 4, 6, 1, 3, 2, 6, 15, 7, 0]\n#prices = [17, 16, 14, 13, 11, 5, 0]\nmax = [0, 0, 0]\nfor i in 0...prices.length do\n    for j in i...prices.length do\n        k = prices[j]-prices[i]\n        max = [k, i, j] if k > max[0]\n    end\nend\nputs max[0]==0 ?  \"no luck\" : max.inspect\n","undoManager":{"mark":87,"position":100,"stack":[[{"group":"doc","deltas":[{"start":{"row":0,"column":18},"end":{"row":0,"column":19},"action":"insert","lines":[" "]}]}],[{"group":"doc","deltas":[{"start":{"row":0,"column":19},"end":{"row":0,"column":20},"action":"insert","lines":["a"]},{"start":{"row":0,"column":20},"end":{"row":0,"column":21},"action":"insert","lines":["d"]}]}],[{"group":"doc","deltas":[{"start":{"row":0,"column":20},"end":{"row":0,"column":21},"action":"remove","lines":["d"]}]}],[{"group":"doc","deltas":[{"start":{"row":0,"column":19},"end":{"row":0,"column":20},"action":"remove","lines":["a"]}]}],[{"group":"doc","deltas":[{"start":{"row":0,"column":19},"end":{"row":0,"column":20},"action":"insert","lines":["d"]}]}],[{"group":"doc","deltas":[{"start":{"row":0,"column":20},"end":{"row":0,"column":21},"action":"insert","lines":["a"]}]}],[{"group":"doc","deltas":[{"start":{"row":0,"column":21},"end":{"row":0,"column":22},"action":"insert","lines":["u"]}]}],[{"group":"doc","deltas":[{"start":{"row":0,"column":21},"end":{"row":0,"column":22},"action":"remove","lines":["u"]}]}],[{"group":"doc","deltas":[{"start":{"row":0,"column":21},"end":{"row":0,"column":22},"action":"insert","lines":["y"]}]}],[{"group":"doc","deltas":[{"start":{"row":0,"column":22},"end":{"row":0,"column":23},"action":"insert","lines":["s"]}]}],[{"group":"doc","deltas":[{"start":{"row":0,"column":23},"end":{"row":0,"column":24},"action":"insert","lines":[" "]}]}],[{"group":"doc","deltas":[{"start":{"row":0,